import {useState} from "react";

export const State =() => {
 let [state, setState] = useState({
        profile: {
            posts: [
                {id: 1, message: 'Hello, how you doin?', likesCount: 12},
                {id: 2, message: 'Hi, great, thanx', likesCount: 10},
            ],
        },
        messagesPage: {
            dialogs: [
                {id: 1, name: 'Alex'},
                {id: 2, name: 'Igor'},
                {id: 3, name: 'Leha'},
                {id: 4, name: 'Gleb'}
            ],
            messages: [
                {id: 1, message: 'Hi'},
                {id: 2, message: 'Hi Hi how are you?'},
                {id: 3, message: 'Hi Hi Hi'}
            ]
        },
        sidebar: {
            friends: [
                {id: 2, name: 'Igor'},
                {id: 3, name: 'Leha'},
                {id: 4, name: 'Gleb'}
            ]
        },
    })
// ...man5,
//         mother: {...man5.mother, work: {...man5.mother.work}, parents: man5.mother.parents.map(el => ({...el}))}
const addPost = (postMessage: string) => {
        let newPost = {id: 5, message: postMessage, likesCount: 0}
        setState({...state, profile: {...state.profile, posts: state.profile.posts.map(el => ({...el, newPost}))}})
    }
}
