// import {rerenderEntireTree} from "./render";



import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";
import { BrowserRouter } from "react-router-dom";


let state = {
    profile: {
        posts: [
            {id: 1, message: 'Hello, how you doin?', likesCount: 12},
            {id: 2, message: 'Hi, great, thanx', likesCount: 10},
        ],
    },
    messagesPage: {
        dialogs: [
            {id: 1, name: 'Alex'},
            {id: 2, name: 'Igor'},
            {id: 3, name: 'Leha'},
            {id: 4, name: 'Gleb'}
        ],
        messages: [
            {id: 1, message: 'Hi'},
            {id: 2, message: 'Hi Hi how are you?'},
            {id: 3, message: 'Hi Hi Hi'}
        ]
    },
    sidebar: {
        friends: [
            {id: 2, name: 'Igor'},
            {id: 3, name: 'Leha'},
            {id: 4, name: 'Gleb'}
        ]
    },
}

const addPost = (postMessage: string) => {
    const newPost = { id: 5, message: postMessage, likesCount: 0 };
    const updatedProfile = { ...state.profile, posts: [...state.profile.posts] };
    updatedProfile.posts.push(newPost);
    state = {...state, profile: updatedProfile,};
};

ReactDOM.render(
    <BrowserRouter>
        <App
            addPost={addPost}
            posts={state.profile.posts}
            messages={state.messagesPage.messages}
            dialogs={state.messagesPage.dialogs}
        />
    </BrowserRouter>,
    document.getElementById("root")
);
