import React, {useState} from "react";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";
import { BrowserRouter } from "react-router-dom";

const dialogs = [
    { id: 1, name: "Alex" },
    { id: 2, name: "Igor" },
    { id: 3, name: "Leha" },
    { id: 4, name: "Gleb" },
];

const messages = [
    { id: 1, message: "Hi" },
    { id: 2, message: "Hi Hi how are you?" },
    { id: 3, message: "Hi Hi Hi" },
];

const initialPosts = [
    { id: 1, message: "Hello, how you doin?", likesCount: 12 },
    { id: 2, message: "Hi, great, thanx", likesCount: 10 },
];

const AppContainer = () => {
    const [posts, setPosts] = useState(initialPosts);

    const addPost = (postMessage: string) => {
        const newPost = {id: posts.length + 1, message: postMessage, likesCount: 0};
        setPosts([...posts, newPost]);
    };
    ReactDOM.render(
        <BrowserRouter>
            <App
                dialogs={dialogs}
                messages={messages}
                posts={posts}
                addPost={addPost}
            />
        </BrowserRouter>,
        document.getElementById("root")

    );
}
