import React, { useState } from "react";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";
import { BrowserRouter } from "react-router-dom";

const initialPosts = [
    { id: 1, message: "Hello, how you doin?", likesCount: 12 },
    { id: 2, message: "Hi, great, thanx", likesCount: 10 },
];

const AppContainer = () => {
    const [posts, setPosts] = useState(initialPosts);

    const addPost = (postMessage: string) => {
        const newPost = { id: posts.length + 1, message: postMessage, likesCount: 0 };
        setPosts([...posts, newPost]);
    };

    return (
        <BrowserRouter>
            <App posts={posts} addPost={addPost} />
        </BrowserRouter>
    );
};

ReactDOM.render(<AppContainer />, document.getElementById("root"));
