import React, {ChangeEvent, useState} from "react";
import s from './MyPost.module.css'
import {Post} from "./Post/Post";

type PostsStructureType = {
    id: number
    message: string
    likesCount: number
}
type PostsPropsType = {
    posts: PostsStructureType[]
    addPost: (postMessage: string) => void
    newPostText: string
    updatePostMessage: (newPostText: string) => void
}
export const MyPost = (props: PostsPropsType) => {



    const onChangeHandler = (e: ChangeEvent<HTMLTextAreaElement>) => {
        props.updatePostMessage(e.currentTarget.value)
    }
    const onCLickAddPostHandler = () => {
        if(props.newPostText.trim()){
            props.addPost(props.newPostText)
            props.updatePostMessage('')
        } else {
       return ' '
        }

    }
    const onKeyPressHandler = (e: React.KeyboardEvent<HTMLTextAreaElement>) =>{
        e.key === 'Enter' && e.ctrlKey && onCLickAddPostHandler()
    }

    let postsElement = props.posts.map((p, i) =>
        <Post key={i}
              message={p.message}
              likesCount={p.likesCount}
        />)
    return (
        <div>
            <div className={s.postsBlock}>
                <h2>My posts</h2>
                <div/>
                <div>
                    <textarea  onKeyPress={onKeyPressHandler} value={props.newPostText} onChange={onChangeHandler}/>
                </div>
                <div>
                    <button onClick={onCLickAddPostHandler}>Add post</button>
                </div>
                <div className={s.posts}>
                    {postsElement}
                </div>
            </div>
        </div>
    )
}
